import { Component, OnInit } from '@angular/core';

import { FormGroup, FormBuilder } from '@angular/forms';
import { CrudService } from 'src/app/service/crud.service';
import { Router,ActivatedRoute } from '@angular/router';

@Component({
  selector: 'app-editar-libro',
  templateUrl: './editar-libro.component.html',
  styleUrls: ['./editar-libro.component.css']
})
export class EditarLibroComponent implements OnInit{

  formularioDeLibros:FormGroup;
  theISBN: any;


  constructor(
    private activeRoute : ActivatedRoute,
    private crudService: CrudService,
    public formulario: FormBuilder,
    private ruteador: Router
    ){
      this.theISBN = this.activeRoute.snapshot.paramMap.get('isbn');
      console.log(this.theISBN);
      this.crudService.ObtenerLibro(this.theISBN).subscribe(respuesta => {
        console.log(respuesta);
       this.formularioDeLibros.setValue({
          titulo:respuesta[0]['titulo'],
          autor:respuesta[0]['autor'],
          anio:respuesta[0]['anio'],
          editorial:respuesta[0]['editorial']
        }); 
      });

      this.formularioDeLibros = this.formulario.group({
        titulo: [''],
        autor: [''],
        anio: [''],
        editorial: ['']
      });
  } 


  ngOnInit(): void {

  }


  enviarDatos(){
    console.log(this.theISBN);
    console.log(this.formularioDeLibros.value)
    this.crudService.EditarLibro(this.theISBN, this.formularioDeLibros.value).subscribe(()=>{
      this.ruteador.navigateByUrl('/listar-libro');

    })

  }



}
<div class="card mt-4">
    <div class="card-header">
        Editar libros
    </div>
    <div class="card-body">
        <form [formGroup]="formularioDeLibros" (ngSubmit)="enviarDatos()">
            <div class="mb-3">
                <label for="titulo" class="form-label">Título</label>
                <input formControlName="titulo" type="text"
                    class="form-control"   aria-describedby="helpId" placeholder="Ingresa el título del libro">


            </div>

            <div class="mb-3">
              <label for="autor" class="form-label">Autor</label>
              <input formControlName="autor" type="text"
                class="form-control" aria-describedby="helpId" placeholder="Ingresa el nombre del autor">

            </div>

            <div class="mb-3">
                <label for="anio" class="form-label">Año</label>
                <input formControlName="anio" type="text"
                  class="form-control" aria-describedby="helpId" placeholder="Ingresa el año de edición del libro">

            </div>

            <div class="mb-3">
                <label for="editorial" class="form-label">Editorial</label>
                <input formControlName="editorial" type="text"
                  class="form-control" aria-describedby="helpId" placeholder="Ingresa la editorial del libro">

            </div>

            <div class="btn-group" role="group" aria-label="Button group name">
                <button type="submit" class="btn btn-warning">Modificar</button>
                <button type="button" class="btn btn-danger ms-3" routerLink="/listar-libro">Cancelar</button>
            </div>

        </form>

    </div>

</div>
