import { Component } from '@angular/core';
import { FormGroup, FormBuilder } from '@angular/forms';
import { Route, Router } from '@angular/router';
import{ CrudService } from 'src/app/service/crud.service'

@Component({
  selector: 'app-agregar-libro',
  templateUrl: './agregar-libro.component.html',
  styleUrls: ['./agregar-libro.component.css']
})
export class AgregarLibroComponent {
 /* Recolectamos los datos desde el formulario*/
 formularioDeLibros:FormGroup;

 constructor(public formulario: FormBuilder, 
  private crudService:CrudService, 
  private rutedor:Router){
    this.formularioDeLibros = this.formulario.group({
      isbn:[''],
      titulo:[''],
      autor:[''],
      anio:[''],
      editorial:['']

    });
  }

  enviarDatos(){
    console.log("Presionaste el boton enviar datos");
    console.log(this.formularioDeLibros.value);
    this.crudService.AgregarLibro(this.formularioDeLibros.value).subscribe(respuesta => {
      this.rutedor.navigateByUrl("/listar-libro")

    });


  }

}
<div class="card mt-4">
    <div class="card-header">
        Agregar libros
    </div>
    <div class="card-body">
        <form (ngSubmit)="enviarDatos()" [formGroup] = "formularioDeLibros" >

            <div class="mb-3">
                <label for="" class="form-label">Isbn</label>
                <input formControlName="isbn" type="number"
                    class="form-control"   aria-describedby="helpId" placeholder="Ingresa el título del libro">


            </div>

            <div class="mb-3">
                <label for="" class="form-label">Título</label>
                <input formControlName="titulo" type="text"
                    class="form-control"   aria-describedby="helpId" placeholder="Ingresa el título del libro">


            </div>

            <div class="mb-3">
              <label for="" class="form-label">Autor</label>
              <input formControlName="autor" type="text"
                class="form-control" aria-describedby="helpId" placeholder="Ingresa el nombre del autor">

            </div>

            <div class="mb-3">
                <label for="" class="form-label">Año</label>
                <input formControlName="anio" type="text"
                  class="form-control" aria-describedby="helpId" placeholder="Ingresa el año de edición del libro">

            </div>

            <div class="mb-3">
                <label for="" class="form-label">Editorial</label>
                <input  formControlName="editorial"type="text"
                  class="form-control" aria-describedby="helpId" placeholder="Ingresa la editorial del libro">
            </div>

            <div class="btn-group" role="group" aria-label="Button group name">
                <button type="submit" class="btn btn-primary">Agregar</button>
                <button type="button" class="btn btn-danger ms-3" routerLink="/listar-libro">Cancelar</button>
            </div>

        </form>

    </div>

</div>
